mapper.update:
update lend_list

    backDate=#{backDate},

        backType=#{backType},


        exceptRemarks=#{exceptRemarks}


where id=#{id}

mapper.select:
SELECT lend.id as id, lend.bookId, lend.readerId, lend.lendDate, lend.backDate, lend.backType, lend.exceptRemarks, reader.readerNumber as `readerInfo.readerNumber`, reader.realName AS `readerInfo.realName`, book.name AS `bookInfo.name`, book.author AS `bookInfo.author`, book.isbn AS `bookInfo.isbn`, book.type_id AS `bookInfo.typeId`, type.name AS `bookInfo.typeInfo.name` FROM lend_list lend LEFT JOIN reader_info reader ON reader.id = lend.readerId LEFT JOIN book_info book ON book.id = lend.bookId LEFT JOIN type_info type ON book.type_id = type.id
and book.id=#{bid} and reader.id=#{rid}
order by lend.id desc
